// Validate PAN format and basic client data
const clientData = $json.body || $json;

// PAN validation regex
const panRegex = /^[A-Z]{5}[0-9]{4}[A-Z]{1}$/;

// Aadhaar validation regex (12 digits)
const aadhaarRegex = /^[0-9]{12}$/;

// Email validation
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

// Mobile validation (Indian format)
const mobileRegex = /^[6-9]\d{9}$/;

const validationResults = {
  client_name: clientData.client_name || '',
  mobile: clientData.mobile || '',
  email: clientData.email || '',
  pan_number: (clientData.pan_number || '').toUpperCase(),
  aadhaar_number: clientData.aadhaar_number || '',
  pan_file_url: clientData.pan_file_url || '',
  aadhaar_file_url: clientData.aadhaar_file_url || '',

  // Validation flags
  pan_format_valid: panRegex.test((clientData.pan_number || '').toUpperCase()),
  aadhaar_format_valid: aadhaarRegex.test(clientData.aadhaar_number || ''),
  email_valid: emailRegex.test(clientData.email || ''),
  mobile_valid: mobileRegex.test(clientData.mobile || ''),

  // Overall validation
  basic_validation_passed: false,
  timestamp: new Date().toISOString(),
  workflow_id: $workflow.id + '_' + Date.now()
};

// Check if all basic validations pass
validationResults.basic_validation_passed = 
  validationResults.pan_format_valid && 
  validationResults.aadhaar_format_valid && 
  validationResults.email_valid && 
  validationResults.mobile_valid && 
  validationResults.client_name.length > 0;

return { json: validationResults };

// Process OCR results and compare with user input
const panOcrData = $input.first().json;
const aadhaarOcrData = $input.last().json;
const clientData = $('Validate Client Inputs').first().json;

// Extract PAN details from OCR
let panFromOcr = '';
let nameFromPan = '';
try {
  if (panOcrData.document && panOcrData.document.inference) {
    panFromOcr = panOcrData.document.inference.prediction.id_number?.value || '';
    nameFromPan = panOcrData.document.inference.prediction.given_names?.value || '';
  }
} catch (error) {
  console.log('PAN OCR parsing error:', error);
}

// Extract Aadhaar details from OCR
let aadhaarFromOcr = '';
let nameFromAadhaar = '';
let dobFromAadhaar = '';
try {
  if (aadhaarOcrData.document && aadhaarOcrData.document.inference) {
    aadhaarFromOcr = aadhaarOcrData.document.inference.prediction.id_number?.value || '';
    nameFromAadhaar = aadhaarOcrData.document.inference.prediction.given_names?.value || '';
    dobFromAadhaar = aadhaarOcrData.document.inference.prediction.birth_date?.value || '';
  }
} catch (error) {
  console.log('Aadhaar OCR parsing error:', error);
}

// Compare OCR results with user input
const ocrValidation = {
  ...clientData,

  // OCR extracted data
  pan_from_ocr: panFromOcr,
  name_from_pan: nameFromPan,
  aadhaar_from_ocr: aadhaarFromOcr,
  name_from_aadhaar: nameFromAadhaar,
  dob_from_aadhaar: dobFromAadhaar,

  // Validation flags
  pan_ocr_match: panFromOcr.toUpperCase() === clientData.pan_number.toUpperCase(),
  aadhaar_ocr_match: aadhaarFromOcr === clientData.aadhaar_number,

  // Name similarity check (basic)
  name_similarity_check: nameFromPan.toLowerCase().includes(clientData.client_name.toLowerCase().split(' ')[0]) ||
                        nameFromAadhaar.toLowerCase().includes(clientData.client_name.toLowerCase().split(' ')[0]),

  ocr_validation_passed: false
};

// Overall OCR validation
ocrValidation.ocr_validation_passed = 
  ocrValidation.pan_ocr_match && 
  ocrValidation.aadhaar_ocr_match && 
  ocrValidation.name_similarity_check;

return { json: ocrValidation };

// Process PAN verification results
const panVerificationData = $json;
const clientData = $('Process OCR Results').first().json;

// Extract verification results
let panValid = false;
let panName = '';
let panStatus = '';

try {
  if (panVerificationData.result && panVerificationData.result.pan) {
    panValid = panVerificationData.result.pan.valid === true;
    panName = panVerificationData.result.pan.name || '';
    panStatus = panVerificationData.result.pan.status || '';
  }
} catch (error) {
  console.log('PAN verification parsing error:', error);
}

const finalValidation = {
  ...clientData,

  // PAN verification results
  pan_api_valid: panValid,
  pan_verified_name: panName,
  pan_status: panStatus,

  // Final validation status
  all_validations_passed: false,
  onboarding_status: 'pending',
  verification_timestamp: new Date().toISOString()
};

// Overall validation check
finalValidation.all_validations_passed = 
  finalValidation.basic_validation_passed && 
  finalValidation.ocr_validation_passed && 
  finalValidation.pan_api_valid;

// Set onboarding status
if (finalValidation.all_validations_passed) {
  finalValidation.onboarding_status = 'verified';
} else {
  finalValidation.onboarding_status = 'requires_manual_review';
}

return { json: finalValidation };

